package seco;

public class Aritmetico {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// result is now 3
		int result = 1 + 2;
		System.out.println(result);
		
		//result is now 2
		result = result - 1;
		System.out.println(result);
		
		//result is now 4
		result = result * 2;
		System.out.println(result);
		
		//result is now 2
		result = result / 2;
		System.out.println(result);
		
		//result is now 2
		result = result % 7;
		System.out.println(result);
	}

}
